import './App.css';
import {Link} from 'react-router-dom';
import {UserContext} from './UserContext';
import React, {useContext, useState} from 'react'

function PickLeagues() {

  const [user, setUser] = useContext(UserContext);

  const [userLeagues,setUserLeagues] = useState(user.userLeagues)


  const updateUserLeagues = (e) =>{
    e.preventDefault()

    setUserLeagues(
      (prev) => [...prev, e.target.value]
    )      
  }

  const setUserInfoClickHandler = (e) => {
    e.preventDefault();

    setUser(() => (
        { 
            email: user.email, 
            firstName: user.firstName,
            lastName: user.lastName,
            password: user.password,
            userLeagues: userLeagues,
            userTeams: user.userTeams

        })
    );
}


  return (


    <div>
      {/*TODO maybe have boolean for "first time sign up" or something to Welcome on this screen if
      i'm gong to use it for a user that comes back picking editing their leagues or maybe have edit leages as
  separate page*/}
      <h1>Hi {user.firstName}!</h1>
      <h2>Pick Your Leagues</h2>

      <ul>
        {/*TODO change background of NCAA to green onclick w/ dynamic css */}
        <li>
          <button value="CFP" onClick={updateUserLeagues}>Add CFB +</button>
        </li>

        <li>
          <button value="NFL" onClick={updateUserLeagues}>Add NFL +</button>
        </li>

        <li>
          <button value="NBA" onClick={updateUserLeagues}>Add NBA +</button>
        </li>
      </ul>


      <Link to="./PickTeams">
        <button className="btn btn-dark" onClick={setUserInfoClickHandler}>Next</button>
      </Link>
    </div>
  );
}

export default PickLeagues;
