import './App.css';
import { UserContext } from './UserContext';
import React, { useContext, useEffect, useState, useRef } from 'react'

import { Row, Col, Container, Tab, Tabs, Nav } from 'react-bootstrap'

/*will need to useEffect to auto-populate the teams and league when component mounts*/

function Home() {


  const [user, setUser] = useContext(UserContext);
  const [teamNews, setTeamNews] = useState({});
  const [currentTeam, setCurrentTeam] = useState("")

  const [teamTabs, setTeamTabs] = useState([]);
  const [teamFeedTabs, setTeamFeedTabs] = useState([]);

  const teamsNavRef = useRef();
  useEffect(() => {

    async function fetchTeamNews() {

      const apiKey = '81948a14c62a4740a9d1a123afa96c28'
      const team = user.userTeams[0].team
      const mascot = user.userTeams[0].mascot

      /*getting all news within the past week.*/
      let today = new Date()
      let todayString = today.toISOString().slice(0, 10);

      let dateFrom = new Date();
      dateFrom.setDate(today.getDate() - 7);

      let dateFromString = dateFrom.toISOString().slice(0, 10)

      const newsApi = 'http://newsapi.org/v2/everything?q='
        + team + ' ' + mascot + '&apiKey=' + apiKey + '&from=' + dateFromString + '&to=' + todayString + '&qlnTitle=' + mascot;

      const data = await fetch(newsApi)

      const teamNewsJSON = await data.json()
      setTeamNews(teamNewsJSON);



      const teamNavTabs = user.userTeams.map(
        /*TODO change key to be the name + ID*/
        (team) =>
                  <Nav.Item>
                  <Nav.Link key={team.team + " " + team.mascot} eventKey={team.team + " " + team.mascot}>
                    {team.team + " " + team.mascot}</Nav.Link>
                  </Nav.Item>

      )

      const teamFeedTabs = user.userTeams.map(

        (team) =>  <Tab.Pane key={team.team + " " + team.mascot} eventKey={team.team + " " + team.mascot}>
                      <Tabs id="feed" defaultActiveKey={user.userTeams[0].team + " " + user.userTeams[0].mascot}>
                        <Tab eventKey="generalNews" title="General News">
                          {/*{teamNews.articles[0].content} */}
                          hello general news
                          {/*{console.log(teamNews)}*/}
                        </Tab>
                        <Tab eventKey="scoresSchedule" title="Scores/Schedule">
                            Hello scores and schedule
                        </Tab>
                        <Tab eventKey="socialMedia" title="Social Media">
                            hi social media
                        </Tab>
                      </Tabs>
                  </Tab.Pane>

      )
      console.log(teamNavTabs)

      setTeamTabs(teamNavTabs);
      setTeamFeedTabs(teamFeedTabs);

    }

    fetchTeamNews();



  }, [user])

  return (
    <Container>
      <div className="row">


        <div className="col-sm-3">
          Team Info
        </div>

        <div className="col-sm-9">Feed</div>
        <Tab.Container defaultActiveKey="first">
          <Row>
            <Col sm={3}>
              <Nav variant="pills" className="flex-column">
                {teamTabs}
                <Nav.Item>
                  <Nav.Link eventKey="second">Tab 2</Nav.Link>
                </Nav.Item>
              </Nav>
            </Col>
            <Col sm={9}>
              <Tab.Content>
                {teamFeedTabs}
              </Tab.Content>
            </Col>
          </Row>
        </Tab.Container>
      </div>
    </Container>


  );
}

export default Home;
