import './App.css';
import {Link} from 'react-router-dom';
import {UserContext} from './UserContext';
import React, {useContext, useState} from 'react'

function PickLeagues() {

  const [user, setUser] = useContext(UserContext);

  const [userLeagues,setUserLeagues] = useState(user.leagues)

  const setUserLeaguesClickHandler = (e) =>{
    e.preventDefault()

    setUserLeagues((prevLeagues) =>
      [...prevLeagues,user.leagues]
    )
  }



  console.log(user);
  return (


    <div>
      <h2>Pick Your Leagues</h2>

      <ul>
        {/*change background of NCAA to green onclick w/ dynamic css */}
        <li>
          NCAA Football <button onClick="">+</button>
        </li>

        <li>
          NFL <button onClick={setUserLeaguesClickHandler}>+</button>
        </li>

        <li>
          NBA <button onClick={setUserLeaguesClickHandler}>+</button>
        </li>
      </ul>


      <Link to="./PickTeams">
        <button className="btn btn-dark">Next</button>
      </Link>
    </div>
  );
}

export default PickLeagues;
