import './App.css';
import { Link } from 'react-router-dom';
import { UserContext } from './UserContext';
import React, { useContext, useState, useEffect} from 'react'

function PickTeams() {

  const [user, setUser] = useContext(UserContext);

  const leagueList = user.userLeagues.map(
    (league) => <li key={user.userLeagues.indexOf(league)}>{league}</li>
  )


  let collegeTeams = []
  useEffect(() => {

    async function fetchTeams(){
          /*currently fetching college teams*/
      const data = await fetch('https://api.collegefootballdata.com/teams/fbs')

      const teams = await data.json()

      collegeTeams = teams.map(
        (team) => <option value={team.school}>{team.school}</option>
        )

      console.log(collegeTeams);



    }

    fetchTeams();

  }, [])




  return (

    <div>
      <h2>Pick Your Teams</h2>


      <ul>
        {leagueList}
      </ul>

      <h3>Fetch Teams</h3>

      <select style={{ width: '20%' }}>
        {collegeTeams}
      </select>

      <select>
        <option value="test">Test</option>
        <option>Test2</option>
      </select>

      <button className="btn btn-dark">
        <Link to="./Home">Next</Link>
      </button>

    </div>
  );
}

export default PickTeams;
