import './App.css';
import {Link} from 'react-router-dom';
import {UserContext} from './UserContext';
import React, {useContext, useState} from 'react'

function PickLeagues() {

  const [user, setUser] = useContext(UserContext);

  const [userLeagues,setUserLeagues] = useState(user.userLeagues)

  const [isSelected, setSelected] = useState(false)


  const updateUserLeagues = (e) =>{
    e.preventDefault()

    setUserLeagues(

      (prev) => {

        const league = e.target.value;
        for(let i = 0; i < prev.length; i++){

          if (prev[i] === league){

            return[...prev];
          }
        }

        return [...prev, e.target.value];
      }
    )

    setSelected(
      () => {

        const leagueSel = e.target.value

        for(let i = 0; i < userLeagues.length; i++){

          if(userLeagues[i] === leagueSel){
            return true;
          }
        }

        return false;
      }
    )



  }








  const setUserInfoClickHandler = (e) => {
    e.preventDefault();

    setUser(() => (
        { 
            email: user.email, 
            firstName: user.firstName,
            lastName: user.lastName,
            password: user.password,
            userLeagues: userLeagues,
            userTeams: user.userTeams

        })
    );
}

  return (


    <div>
      {/*TODO maybe have boolean for "first time sign up" or something to Welcome on this screen if
      i'm gong to use it for a user that comes back picking editing their leagues or maybe have edit leages as
  separate page*/}
      <h1>Hi {user.firstName}!</h1>
      <h2>Pick Your Leagues</h2>

      <ul className="btn-pick">
        <li>
          <button className={isSelected ? "btn-green" : ""}  value="CFB" onClick={updateUserLeagues}>Add CFB +</button>
        </li>

        <li>
          <button className={isSelected ? "btn-green" : ""} value="NFL" onClick={updateUserLeagues}>Add NFL +</button>
        </li>

        <li>
          <button className={isSelected ? "btn-green" : ""} value="NBA" onClick={updateUserLeagues}>Add NBA +</button>
        </li>
      </ul>



        <button className="btn btn-dark" onClick={setUserInfoClickHandler}>
          <Link to="./PickTeams">Next</Link>
        </button>

        <div>
        <button className="btn btn-dark">
                <Link to="./Signup">
                    Go Back
                </Link>
        </button>
        </div>


    </div>
  );
}

export default PickLeagues;
