import './App.css';
import { Link } from 'react-router-dom';
import { UserContext } from './UserContext';
import React, { useContext, useState, useEffect, useRef } from 'react'

function PickTeams() {

  const [user, setUser] = useContext(UserContext);

  const [leagueDropDown, setLeagueDropDown] = useState([])

  const leagueList = user.userLeagues.map(
    (league) => <li key={user.userLeagues.indexOf(league)}>{league}</li>
  )

  const teamsRef = useRef();
  useEffect(() => {
    fetchTeams();
  }, [])

  async function fetchTeams() {
    /*fetch college teams*/
    const data = await fetch('https://api.collegefootballdata.com/teams/fbs')

    const teams = await data.json()

    const collegeTeamsChk = teams.map(
      (team) => <div><label key={team.id}><input style = {{display:"in-line"}} type="checkbox" key={team.id} name={team.school} value={team.school} />{"   " + team.school}</label></div>
    )

    teamsRef.current = { collegeTeamsChk };
    setLeagueDropDown(teamsRef.current.collegeTeamsChk)
  }





  return (

    <div>
      <h2>Pick Your Teams</h2>


      <ul>
        {leagueList}
      </ul>

      <h3>Fetch Teams</h3>

      <form>
        <fieldset className="scroll">
          {leagueDropDown}    
        </fieldset>  
      </form>

      <select>
        <option value="test">Test</option>
        <option>Test2</option>
      </select>

      <button className="btn btn-dark">
        <Link to="./Home">Next</Link>
      </button>

    </div>
  );
}

export default PickTeams;
