import './App.css';
import React, { useState, useContext } from 'react'
import { Link, BrowserRouter as Router, Route } from 'react-router-dom';
import PickLeagues from './PickLeagues';
import { UserContext } from './UserContext'


export default function Signup() {

    const [user, setUser] = useContext(UserContext);

    const [email, setEmail] = useState(user.email);
    const [password, setPassword] = useState(user.password);

    const updateEmail = (e) => {
        setEmail(e.target.value)
        console.log(e.target.value)
    }

    const addUser = (e) => {
        e.preventDefault();
        setUser(user =>
            ({ email: email, password: password })
        )
    }
    /*console.log(user);*/


    return (
        <div >
            <h2>Signup {user.email}</h2>

            <form className="signup-form-items" onSubmit={addUser}>

                <label htmlFor="email">Enter email</label>
                <input id="email" type="email" value={email} onChange={updateEmail} placeholder="Email address..."></input>


                <label htmlFor="password">Enter password</label>
                <input id="password" type="password" placeholder="Password..."></input>

                <Link to="./PickLeagues">
                    <button type="submit" className="btn btn-dark">Next</button>
                </Link>


            </form>


            <Link to="./">
                <button className="btn btn-dark">Go Back</button>
            </Link>




        </div>
    )
}
