import './App.css';
import { UserContext } from './UserContext';
import React, { useContext, useEffect, useState, useRef } from 'react'

import { Row, Col, Container, Tab, Tabs, Nav } from 'react-bootstrap'

/*will need to useEffect to auto-populate the teams and league when component mounts*/

function Home() {


  const [user, setUser] = useContext(UserContext);
  const [teamNews, setTeamNews] = useState([]);
  const [currentTeamNews, setCurrentTeamNews] = useState({})


  const [teamTabs, setTeamTabs] = useState([]);
  const [teamFeedTabs, setTeamFeedTabs] = useState([]);

  useEffect(() => {

    async function fetchTeamNews(team, mascot) {

      const apiKey = '81948a14c62a4740a9d1a123afa96c28'

      /*getting all news within the past week.*/
      let today = new Date()
      let todayString = today.toISOString().slice(0, 10);

      let dateFrom = new Date();
      dateFrom.setDate(today.getDate() - 7);

      let dateFromString = dateFrom.toISOString().slice(0, 10)

      const newsApi = 'http://newsapi.org/v2/everything?q='
        + team + ' ' + mascot + '&apiKey=' + apiKey + '&from=' + dateFromString + '&to=' + todayString + '&qlnTitle=' + mascot;

      const data = await fetch(newsApi)

      const teamNewsJSON = await data.json()
      setTeamNews( (prev) => {

      for(let i = 0; i < prev.length; i++){

        if(prev[i] === team + ' ' + mascot ){
          return prev
        }
      }

      return [...prev,team + ' ' + mascot,teamNewsJSON]}
      )

    }




    const teamNavTabs = user.userTeams.map(

        (team) => { const fullTeamName = team.team + " " + team.mascot;
                  return <Nav.Item key={"navItem_" + fullTeamName}>
                  <Nav.Link key={"link_" + fullTeamName} eventKey={fullTeamName}>
                    {fullTeamName}</Nav.Link>
                  </Nav.Item>

        }

      )

      const teamFeedTabs = user.userTeams.map(

        (team) => { const fullTeamName = team.team + " " + team.mascot;

                  return <Tab.Pane key={"pane_" + fullTeamName} eventKey={fullTeamName}>
                      <Tabs key={"tabs_" + fullTeamName} id="feed" defaultActiveKey={user.userTeams[0].team + " " + user.userTeams[0].mascot}>
                        <Tab key={"gn_" + fullTeamName} eventKey="generalNews" title="General News">

                          hello general news
                          {/*{console.log(teamNews)}*/}
                        </Tab>
                        <Tab key={"ss_" + fullTeamName} eventKey="scoresSchedule" title="Scores/Schedule">
                            Hello scores and schedule
                        </Tab>
                        <Tab key={"sm_" + fullTeamName} eventKey="socialMedia" title="Social Media">
                            hi social media
                        </Tab>
                      </Tabs>
                  </Tab.Pane>
        }

      )
      setTeamTabs(teamNavTabs);
      setTeamFeedTabs(teamFeedTabs);

      for(let i = 0; i < user.userTeams.length; i++){
        fetchTeamNews(user.userTeams[i].team,user.userTeams[i].mascot);
      }


  }, [user])



  return (
    <Container>
      <div className="row">


        <div className="col-sm-3">
          Team Info
          {console.log(teamNews)}
        </div>

        <div className="col-sm-9">Feed</div>
        <Tab.Container defaultActiveKey={user.userTeams[0].team + " " + user.userTeams[0].mascot}>
          <Row>
            <Col sm={3}>
              <Nav variant="pills" className="flex-column">
                {teamTabs}
              </Nav>
            </Col>
            <Col sm={9}>
              <Tab.Content>
                {teamFeedTabs}
              </Tab.Content>
            </Col>
          </Row>
        </Tab.Container>
      </div>
    </Container>


  );
}

export default Home;
