import './App.css';
import {Link} from 'react-router-dom';
import {UserContext} from './UserContext';
import React, {useContext, useState} from 'react'

function PickLeagues() {

  const [user, setUser] = useContext(UserContext);

  const [userLeagues,setUserLeagues] = useState(user.leagues)

  const setUserLeaguesClickHandler = (e) =>{
    e.preventDefault()

    const league = e.target.value;

    setUserLeagues((prevLeagues) =>
      [...prevLeagues,league]
    )
  }



  console.log(user);
  return (


    <div>
      {/*TODO maybe have boolean for "first time sign up" or something to Welcome on this screen if
      i'm gong to use it for a user that comes back picking editing their leagues or maybe have edit leages as
  separate page*/}
      <h1>Hi {user.firstName}!</h1>
      <h2>Pick Your Leagues</h2>

      <ul>
        {/*TODO change background of NCAA to green onclick w/ dynamic css */}
        <li>
          NCAA Football <button value="CFP" onClick={setUserLeaguesClickHandler}>Add CFB +</button>
        </li>

        <li>
          NFL <button onClick={setUserLeaguesClickHandler}>Add NFL +</button>
        </li>

        <li>
          NBA <button onClick={setUserLeaguesClickHandler}>Add NBA +</button>
        </li>
      </ul>


      <Link to="./PickTeams">
        <button className="btn btn-dark">Next</button>
      </Link>
    </div>
  );
}

export default PickLeagues;
