import './App.css';
import { Link } from 'react-router-dom';
import { UserContext } from './UserContext';
import React, { useContext, useState, useEffect, useRef } from 'react'
import Navbar from 'react-bootstrap/Navbar';

function PickTeams() {

  const [user, setUser] = useContext(UserContext);

  const [userTeams, setUserTeams] = useState(user.userTeams)

  const [cfbDropDown, setCFBDropDown] = useState([])

  const [isCFBSelected, setIsCFBSelected] = useState(false)
  const [isNFLSelected, setIsNFLSelected] = useState(false)
  const [isNBASelected, setIsNBASelected] = useState(false)

  const leagueList = user.userLeagues.map(
    (league) => <li key={user.userLeagues.indexOf(league)}>{league}</li>
  )

  const teamsRef = useRef();
  useEffect(() => {

    async function fetchTeams() {

      /*fetch college teams*/
      const data = await fetch('https://api.collegefootballdata.com/teams/fbs')

      const teams = await data.json()

      const collegeTeamsChk = teams.map(
        (team) => <div key={team.id}>
          <label>
            <input onChange={setCFBTeamsChangeHandler} style={{ display: "in-line" }} type="checkbox" key={team.id} name={team.school} value={team.school} />
            {" " + team.school}
          </label>
        </div>
      )

      teamsRef.current = { collegeTeamsChk };
      setCFBDropDown(teamsRef.current.collegeTeamsChk)
    }
    fetchTeams();



    const setSelectedValues = () =>{

      for(let i = 0; i < user.userLeagues.length;i++){

        if(user.userLeagues[i] === "CFB"){
          setIsCFBSelected(true);

        }else if(user.userLeagues[i] === "NFL"){
          setIsNFLSelected(true);

        }else if(user.userLeagues[i] === "NBA"){
          setIsNBASelected(true);

        }else{
          throw "Invalid league " & user.userLeagues[i];
        }

      }
    }
    setSelectedValues();
  },[])

  const setCFBTeamsChangeHandler = (e) => {
    /*do not prevent default, we want the click*/
    setUserTeams( (prev) => {

      if(e.target.checked){

        return[...prev,{
          league: "CFB",
          team: e.target.value
        }]

      }else{

        let found = false;
        for(let i = 0; i < prev.length; i++){

          if(prev[i].league === "CFB" && prev[i].team === e.target.value && !found){
            prev.splice(i,1);
            found = true;
          }
        }
        return prev;      
      }
    }    
    )

  }

  const setUserInfoClickHandler = (e) => {
    e.preventDefault();

    setUser(() => (
        { 
            email: user.email, 
            firstName: user.firstName,
            lastName: user.lastName,
            password: user.password,
            userLeagues: user.userLeagues,
            userTeams: userTeams

        })
    );
}


  return (

    <div>
      <h2>Pick Your Teams</h2>
      <ul>
        {leagueList}
      </ul>

      <h3>College Teams</h3>

      <form name="teamsPickForm">
        <fieldset className = {isCFBSelected ? "scroll" : "hidden"} name="cfbTeamsPick" form="teamsPickForm">
          {cfbDropDown}
        </fieldset>
      </form>

      <button className="btn btn-dark" onClick={setUserInfoClickHandler}>
        <Link to="./Home">
          Next</Link>
      </button>
    </div>
  );
}

export default PickTeams;
