import './App.css';
import { UserContext } from './UserContext';
import React, { useContext, useEffect } from 'react'
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';

/*will need to useEffect to auto-populate the teams and league when component mounts*/

function Home() {

  useEffect(() => {

    async function fetchTeamNews() {

      const apiKey = '81948a14c62a4740a9d1a123afa96c28'
      const team = user.userTeams[0].team
      const mascot = 'Gamecocks'

      let [month, date, year] = new Date().toLocaleDateString("en-US").split("/");

      console.log(month);
      console.log(date);

      let today = new Date()
      let todayString = today.toISOString().slice(0,10);

      let dateFrom = new Date();
      dateFrom.setDate(today.getDate() - 365)

      let dateFromString = dateFrom.toISOString().slice(0,10)

      /*getting all news within the past week.*/
      const sport = 'football'

      const newsApi = 'http://newsapi.org/v2/everything?q=' 
      + team + ' ' + mascot + sport + '&apiKey=' + apiKey + '&from=' + dateFromString + '&to=' + todayString; 
      /*fetch college teams*/
      const data = await fetch(newsApi)
      /*if (data.status != 200) {
        throw "Issue getting API data Code:" + data.code + " Message: " + data.message;
      }*/

      const teamNews = await data.json()

      console.log(teamNews) 
    }
    fetchTeamNews();

  })

  const [user, setUser] = useContext(UserContext);


  console.log(user);

  return (
    <div className="container">

      <div className="row">

        <div className="col-sm-2">
          Leagues <br />
          {user.userTeams[0].league}
        </div>

        <div className="col-sm-2">
          Team Info <br />
          {user.userTeams[0].team}
        </div>

        <div className="col-sm-8">Feed
        <Tabs defaultActiveKey="generalNews">

            <Tab eventKey="generalNews" title="General News">
              Hello Mars
            </Tab>
            <Tab eventKey="scoresSchedule" title="Scores/Schedule">
              Hello Earn
            </Tab>
            <Tab eventKey="socialMedia" title="Social Media">
              hi
            </Tab>
          </Tabs>
        </div>
      </div>
    </div>

  );
}

export default Home;
