import './App.css';
import { UserContext } from './UserContext';
import React, { useContext } from 'react'
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab';

/*will need to useEffect to auto-populate the teams and league when component mounts*/

function Home() {

  const [user, setUser] = useContext(UserContext);

  console.log(user);

  return (
    <div className="container">

      <div className="row">

        <div className="col-sm-2">
          Leagues <br />
          {user.userTeams[0].league}
        </div>

        <div className="col-sm-2">
          Team Info <br />
          {user.userTeams[0].team}
        </div>

        <div className="col-sm-8">Feed
        <Tabs defaultActiveKey="generalNews" id="uncontrolled-tab-example">

            <Tab eventKey="generalNews" title="General News">
              Hello Mars
            </Tab>
            <Tab eventKey="scoresSchedule" title="Scores/Schedule">
              Hello Earn
            </Tab>
            <Tab eventKey="socialMedia" title="Social Media">
              hi
            </Tab>
          </Tabs>
        </div>
      </div>
    </div>

  );
}

export default Home;
