import './App.css';
import React, { useState, useContext } from 'react'
import { Link, BrowserRouter as Router, Route } from 'react-router-dom';
import PickLeagues from './PickLeagues';
import { UserContext } from './UserContext'


export default function Signup() {

    const [user, setUser] = useContext(UserContext);
    const [email, setEmail] = useState(user.email);
    const [password, setPassword] = useState(user.password);

    const updateEmail = (e) => {
        setEmail(e.target.value);
    }

    const updatePassword = (e) => {
        setPassword(e.target.value);
    }

    const setUserInfoClickHandler = (e) => {
        e.preventDefault();
        console.log(email);
        setUser(() => ({ email: email, password: password })
        );
    }

    return (
        <div >
            <h2>Signup</h2>

            <form>
                <label htmlFor="email">Enter email</label>
                <input id="email" type="email" value={email} onChange={updateEmail} placeholder="Email address..."></input>

                <label htmlFor="firstName">Enter first name</label>
                <input id="firstName" type="text" value={email} onChange={updateEmail} placeholder="Email address..."></input>

                <label htmlFor="lastName">Enter first name</label>
                <input id="lastName" type="text" value={email} onChange={updateEmail} placeholder="Email address..."></input>                


                <label htmlFor="password">Enter password</label>
                <input id="password" type="password" placeholder="Password..."></input>
            </form>


            <button onClick={setUserInfoClickHandler} className="btn btn-dark">
                <Link to="./PickLeagues">
                    Next
                </Link>
            </button>


            <button className="btn btn-dark">
                <Link to="./Login">
                    Go Back
                </Link>
            </button>

        </div>
    )
}
